import React, { useEffect } from 'react'
import { Field, ErrorMessage, Formik } from 'formik'
import { connect } from 'react-redux'

import * as actions from '../../../backend/store/actions'
import LogInSchema from './LogInSchemas.js'

import { StyledForm } from '../../../style/UI/FormWrappers.js'
import Input from '../../../style/UI/Input.js'

const LogInForm = ({ login, loading, error, cleanUp }) => {
  useEffect(() => {
    return () => {
      cleanUp()
    }
  }, [cleanUp])

  return(
    <Formik
      initialValues = {{
        email: "",
        password: "",
      }}
      validationSchema = {LogInSchema}
      onSubmit = {async ( values, { resetForm, setSubmitting }) => {
        await login(values)
        resetForm()
        setSubmitting(false)
      }}
    >
      {({ errors, touched, isSubmitting }) => (
        <StyledForm>

            <Field name = "email" type = "email" placeholder = "Your email..." component = {Input}/> <br/>
            <Field name = "password" type = "password" placeholder = "Your password..." component = {Input}/> <br/>

          <p>{error}</p>
          <button type = "submit" disabled = {isSubmitting} className = "classic-button"> Log In </button>

        </StyledForm>
      )}
    </Formik>
  )
}

const mapStateToProps = ({ auth }) => ({
  loading: auth.loading,
  error: auth.error,
})

const mapDispatchToProps = {
  login: actions.logIn,
  cleanUp: actions.clean,
}

export default connect(mapStateToProps, mapDispatchToProps)(LogInForm)
