import React, { useEffect } from 'react'
import { Formik, ErrorMessage } from 'formik'
import styled from 'styled-components'

import { SignUpSchema } from './SignUpSchema.js'
import Heading from '../../../style/UI/Heading.js'
import { StyledForm } from '../../../style/UI/FormWrappers.js'
import Button from '../../../style/UI/Buttons.js'

const Wrapper = styled.div`
  width: 100%;
  display: flex;
  justify-content: flex-end;
`

const SignUpForm = ({ login, loading, error, cleanUp }) => {
  useEffect(() => {
    return () => {
      cleanUp()
    }
  }, [cleanUp])

  return (
    <Formik
      initialValues = {{
        firstName: "",
        lastName: "",
        username: "",
        email: "",
        phone: "",
        passwordOne: "",
        passwordTwo: "",
        creditCard: "",
        expDate: "",
        secCode: "",
        creditCardType: "",
        apps: [false, false, false, false], // Postamtes, GrubHub, DoorDash, UberEats
      }}
      validationSchema = {SignUpSchema}
      onSubmit = {async ( values, { resetForm, setSubmitting }) => {
        await signUp(values)
        resetForm()
        setSubmitting(false)
      }}
    >
      {({ isSubmitting, isValid }) => (

        <StyledForm>

          {React.cloneElement(activePage, { parentState: {...props} })}

          <Wrapper>
            {page > 0 && <Button type = "button" color = {true} onClick={this.previous}> Previous </Button>}
            {!isLastPage && <Button type = "submit" disabled = {!props.isValid || props.isSubmitting}> Next </Button>}
            {isLastPage && <Button type = "submit" color = {props.isValid} disabled = {props.isSubmitting}> Submit </Button>}
          </Wrapper>

        </StyledForm>
      )}
    </Formik>
  )
}

const mapStateToProps = ({ auth }) => ({
  loading: auth.loading,
  error: auth.error,
})

const mapDispatchToProps = {
  signUp: actions.signUp,
  cleanUp: actions.clean,
}

export default connect(mapStateToProps, mapDispatchToProps)(SignUpForm)
