import React from 'react'
import styled from 'styled-components'
import { NavLink } from 'react-router-dom'
import * as ROUTES from '../../constants/routes'

const LinkText = styled.p`
    font-size: 1.5rem;
    font-weight: 400;
    letter-spacing: 0.5px;
    color: var(--color-title);
`
const UnderLink = styled(NavLink)`
    text-transform: uppercase;
    border-bottom: '1px solid transparent';
    padding: ${props => (props.mobile ? '.5rem 1rem' : '1rem')};
    margin: ${props => (props.mobile ? '2rem 0' : '0 1rem')};
    font-weight: 700;
    color: var(--color-title);
    transition: all 0.2s;

    :hover {
    border-bottom: ${props =>
        props.mobile
        ? '1px solid var(--color-white)'
        : '2px solid var(--color-main)'};
    }

    &.active {
    border-bottom: ${props =>
        props.mobile
        ? '1px solid var(--color-white)'
        : '2px solid var(--color-main)'};
    }
`
export const SignUpLink = () => (
    <LinkText>
        <span> New to FeastMode? </span>
        <UnderLink link = {ROUTES.SIGN_UP}> Sign Up </UnderLink>
    </LinkText>
)
