import React from 'react'
import { connect } from 'react-redux'
import FileUploader from "react-firebase-file-uploader"
import * as actions from '../../../../backend/store/actions'
import { storage } from 'firebase'
import styled from 'styled-components'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTrashAlt, faCloudUploadAlt } from '@fortawesome/free-solid-svg-icons'

import Button from '../../../../style/FormUI/Buttons.js'

const ImageForm = styled.form`
    margin-bottom: 1rem;
    width: 74%;
    display: flex;
    align-items: center;

    button {
        opacity: 0;
        transition: all 0.2s;
    }

    :hover button {
        opacity: 1;
    }
`
const StyledImage = styled.img`
    border-radius: 50%; 
    align-items: center;
    display: block;
    margin-left: auto;
    margin-right: auto;
    width: 25rem;
    height: 25rem;
    object-fit: cover;
`
const DeleteButton = styled(Button)`
    font-family: FontAwesome;
    content: '\f107';
    font-size: 28px;
    position: absolute;
    top: 12px;
    right: 20px;
    color: red;
    pointer-events: none;
`

const ImageUpload = ({ updateImageUrl, profile }) => {
    if (!profile.isLoaded) return null

    let state = {
        avatar: "",
        avatarURL: profile.image[0],
        oldAvatar: profile.image[1]
    }

    const handleUploadSuccess = filename => {
        state.avatar = filename
        storage()
            .ref("images/")
            .child(filename)
            .getDownloadURL()
            .then(url => {
                state.avatarURL = url
                updateImageUrl(state)
            }
        )
    }

    return (
        <ImageForm>
            <label>
                <FontAwesomeIcon icon = {faCloudUploadAlt} size = "4x" color = "#ff870C" />
                <FileUploader
                    hidden
                    accept = "image/*"
                    name = "avatar"
                    randomizeFilename
                    storageRef = {storage().ref("images")}
                    onUploadSuccess = {handleUploadSuccess}
                />
            </label>
            {state.avatarURL && <StyledImage src = {state.avatarURL}/>}
            <DeleteButton contain/>
        </ImageForm>
    )
}

const mapStateToProps = ({ firebase }) => ({
    profile: firebase.profile,
})

const mapDispatchToProps = {
    updateImageUrl: actions.updateImageUrl,
}

export default connect(mapStateToProps, mapDispatchToProps)(ImageUpload);
