import React, { Component } from 'react'
import { Formik } from 'formik'

import { SignUpSchemas } from './SignUpSchema.js'

class Wizard extends Component {
  static Page = ({ children }) => children

  constructor(props) {
    super(props)
    this.state = {
      page: 0,
      values: props.initialValues,
    }
  }

  next = values =>
    this.setState(state => ({
      page: Math.min(state.page + 1, this.props.children.length - 1),
      values,
  }))

  previous = () =>
    this.setState(state => ({
      page: Math.max(state.page - 1, 0),
  }))

  handleSubmit = (values, bag) => {
    const { children, onSubmit } = this.props
    const { page } = this.state
    const isLastPage = page === React.Children.count(children) - 1
    if (isLastPage) {
      return onSubmit(values, bag)
    } else {
      bag.setTouched({})
      bag.setSubmitting(false)
      this.next(values)
      console.log("Next")
    }
  }

  render() {
    const { children } = this.props
    const { page, values } = this.state
    const activePage = React.Children.toArray(children)[page]
    const isLastPage = page === React.Children.count(children) - 1

    return (
      <Formik
        initialValues = {values}
        enableReinitialize = {false}
        validationSchema = {SignUpSchemas[page]}
        onSubmit = {this.handleSubmit}
        render = {({ errors, touched, handleSubmit, isSubmitting }) => (
          <form onSubmit = {handleSubmit} className = "classic-form">
            {activePage}
            <div>

              {page > 0 && <button type = "button" className = "classic-button" onClick={this.previous}> Previous </button>}
              {!isLastPage && <button type = "submit" className = "classic-button"> Next </button>}
              {isLastPage && <button type = "submit" disabled = {isSubmitting} className = "classic-button"> Submit </button>}

            </div>

          </form>
        )}
      />
    )
  }
}

export default Wizard
