import React, { useEffect } from 'react'
import { Field, Formik } from 'formik'
import * as actions from '../../../backend/store/actions'
import { connect } from 'react-redux'
import styled from 'styled-components'

import { SignUpSchema } from './SignUpSchema.js'
import { StyledForm } from '../../../style/UI/FormWrappers.js'
import Button from '../../../style/UI/Buttons.js'
import { TextInput } from '../../../style/UI/Inputs.js'
import { Message, MessageWrapper } from '../../../style/UI/Message.js'

const AlignedWrapper = styled.div`
  width: 100%;
  display: flex;
  justify-content: space-between;

  div {
    width: 48%;
  }
`

const SignUpForm = ({ signUp, cleanUp, error, loading }) => {
  useEffect(() => {
    return () => {
      cleanUp()
    }
  }, [cleanUp])

  return (
    <Formik
      initialValues = {{
        firstName: "",
        lastName: "",
        username: "",
        email: "",
        phone: "",
        passwordOne: "",
        passwordTwo: "",
        creditCard: "",
        expDate: "",
        secCode: "",
        creditCardType: "",
        apps: [false, false, false, false], // Postamtes, GrubHub, DoorDash, UberEats
      }}
      validationSchema = {SignUpSchema}
      onSubmit = {async ( values, { resetForm, setSubmitting }) => {
        await signUp(values)
        resetForm()
        setSubmitting(false)
      }}
    >
      {({ isSubmitting, isValid }) => (
        <StyledForm>

          <AlignedWrapper>
            <Field name = "firstName" type = "text" component = {TextInput} labelName = "First Name"/>
            <Field name = "lastName" type = "text" component = {TextInput} labelName = "Last Name"/>
          </AlignedWrapper>
          <Field name = "username" type = "text" component = {TextInput} labelName = "Username"/>
          <Field name = "email" type = "email" component = {TextInput} labelName = "Emal"/>
          <Field name = "phone" type = "text" component = {TextInput} labelName = "Phone"/>
          <AlignedWrapper>
            <Field name = "passwordOne" type = "password" component = {TextInput} labelName = "Password"/>
            <Field name = "passwordTwo" type = "password" component = {TextInput} labelName = "Confirm Password"/>
          </AlignedWrapper>
          <Field name = "creditCard" type = "text" component = {TextInput} label/>
          <AlignedWrapper>
              <Field name = "expDate" type = "text" component = {TextInput}/>
              <Field name = "secCode" type = "text" component = {TextInput}/>
          </AlignedWrapper>
          <Field name = "creditCardType" component = "select" >
              <option value = "" label = "Select one" />
              <option value = "amex" label = "American Express" />
              <option value = "visa" label = "Visa" />
              <option value = "mastercard" label = "Mastercard" />
          </Field>

          <Button
            disabled = {!isValid || isSubmitting}
            loading = {loading ? 'Creating account...' : null}
            type = "submit"
          > 
            Sign Up 
          </Button>

          <MessageWrapper>
            <Message error show = {error}>{ error }</Message>
          </MessageWrapper>

        </StyledForm>
      )}
    </Formik>
  )
}

const mapStateToProps = ({ auth }) => ({
  loading: auth.loading,
  error: auth.error,
})

const mapDispatchToProps = {
  signUp: actions.signUp,
  cleanUp: actions.clean,
}

export default connect(mapStateToProps, mapDispatchToProps)(SignUpForm)
