const INITIAL_STATE = {
    firstName: '',
    lastName: '',
    username: '',
    passwordOne: '',
    passwordTwo: '',
    email: '',
    phone: '',
    touched: {
        firstName: false,
        lastName: false,
        username: false,
        passwordOne: false,
        passwordTwo: false,
        email: false,
        phone: false,
    }
};

class SignUpFormBase extends Component {
    constructor(props) {
        super(props);
        this.state = { ...INITIAL_STATE };
    }

    handleSubmit = event => {
        const { username, email, phone, passwordOne, passwordTwo } = this.state;

        event.preventDefault();

        this.props.firebase
            .doCreateUserWithEmailAndPassword(email, passwordOne)
            .then(authUser => {
            // Create a user in your Firebase realtime database
            return this.props.firebase
                .user(authUser.user.uid)
                .set({
                username,
                email,
                phone,
                })
            })
            .then(() => {
            this.setState({ ...INITIAL_STATE });
            this.props.history.push('/pay');
            })
            .catch(error => {
            this.setState({ error });
            });
    }

    handleChange = event => {
        this.setState({ [event.target.name]: event.target.value });
    }

    handleBlur = (field) => (evt) => {
        this.setState({
            touched: {
                ...this.state.touched, [field]: true
            }
        })
    }

    validate = () => {
        return {
            firstName: this.state.firstName.length == 0,
            lastName: this.state.lastName.length == 0,
            username: this.state.username.length == 0,
            passwordOne: this.state.passwordOne.length == 0,
