export const getCreditCardType = accountNumber => {
  let result = "unknown"

  if (/^5[1-5]/.test(accountNumber)) { result = "MasterCard" } // MasterCard
  else if (/^4/.test(accountNumber)) { result = "Visa" } //Visa
  else if (/^3[47]/.test(accountNumber)) { result = "American Express" } // AmEx

  return result
}

export const displayCreditCardType = (type) => {
    switch (type) {
        case "mastercard":
            break;

        case "visa":
            break;

        case "amex":
            break;

        default:
            break;
    }
}

export const isValidIdentifier = identifier => {
    let sum = 0,
        alt = false,
        i       = identifier.length-1,
        num;

    while (i >= 0){

        //get the next digit
        num = parseInt(identifier.charAt(i), 10);

        //if it's not a valid number, abort
        if (isNaN(num)){
            return false;
        }

        //if it's an alternate number...
        if (alt) {
            num *= 2;
            if (num > 9){
                num = (num % 10) + 1;
            }
        } 

        //flip the alternate bit
        alt = !alt;

        //add to the rest of the sum
        sum += num;

        //go to next digit
        i--;
    }

    //determine if it's valid
    return (sum % 10 == 0);
}
