import React, { useState } from 'react'
import styled from 'styled-components'

import { FormWrapper } from '../../../style/FormUI/FormWrappers.js'
import Heading from '../../../style/FormUI/Heading.js'

const ItemWrapper = styled(FormWrapper)`
    box-shadow: none;
    padding: 0rem;
    margin: 0rem;
    max-width: 1000rem;
    flex-direction: row;
    justify-content: space-between;
`
const Info = styled.div`
    padding: 2rem;
    width: 70%;
`
const ImgWrapper = styled.div`
    width: 30%;
    height: 100%;
`
const Img = styled.img`
    width: 100%;
    height: 100%;
`

(function ($) {
    $.fn.numberPicker = function() {
      var dis = 'disabled';
      return this.each(function() {
        var picker = $(this),
            p = picker.find('button:last-child'),
            m = picker.find('button:first-child'),
            input = picker.find('input'),                 
            min = parseInt(input.attr('min'), 10),
            max = parseInt(input.attr('max'), 10),
            inputFunc = function(picker) {
              var i = parseInt(input.val(), 10);
              if ( (i <= min) || (!i) ) {
                input.val(min);
                p.prop(dis, false);
                m.prop(dis, true);
              } else if (i >= max) {
                input.val(max);
                p.prop(dis, true); 
                m.prop(dis, false);
              } else {
                p.prop(dis, false);
                m.prop(dis, false);
              }
            },
            changeFunc = function(picker, qty) {
              var q = parseInt(qty, 10),
                  i = parseInt(input.val(), 10);
              if ((i < max && (q > 0)) || (i > min && !(q > 0))) {
                input.val(i + q);
                inputFunc(picker);
              }
            };
        m.on('click', function(){changeFunc(picker,-1);});
        p.on('click', function(){changeFunc(picker,1);});
        input.on('change', function(){inputFunc(picker);});
        inputFunc(picker); //init
      });
    };
  }(jQuery));


  $(document).on('ready', function(){

    $('.plusminus').numberPicker();

    //add dynamically:
    $('<div class="plusminus horiz"><button></button><input type="number" name="qty" value="1" min="1" max="5"><button></button></div>').numberPicker().appendTo('body');

  });


const MenuItem = ({ name, picture, description, price}) => {
    const [amount, setAmount] = useState(0);

    const handleClick = () => {
        setAmount(amount + 1)
        console.log(amount)
    }

    return (
        <ItemWrapper onClick={handleClick}>
            <Info>
                <Heading noMargin left bold size = "h3"> {name} </Heading>
                <Heading noMargin left size = "h4"> {description} </Heading>
                <Heading noMargin left bold size = "h4" color = "main"> {price} </Heading>
                <Heading noMargin left bold size = "h4" color = "main"> Amount: {amount} </Heading>
                <Button> Hey</Button>
            </Info>
            <ImgWrapper><Img src = {picture} alt = {name}/></ImgWrapper>

        </ItemWrapper> 
    )
}

export default MenuItem
