import React, { Component } from 'react'
import { Formik } from 'formik'
import styled from 'styled-components'

import { SignUpSchemas } from './SignUpSchemas.js'
import Heading from '../../../style/UI/Heading.js'
import { StyledForm } from '../../../style/UI/FormWrappers.js'
import Button from '../../../style/UI/Buttons.js'
import { Message, MessageWrapper } from '../../../style/UI/Message.js'

const Wrapper = styled.div`
  width: 100%;
  display: flex;
  justify-content: space-between;
`

class Wizard extends Component {
  static Page = ({ children, parentState }) => {
    return children(parentState)
  }

  constructor(props) {
    super(props)
    this.state = {
      page: 0,
      values: props.initialValues,
    }
  }

  next = values =>
    this.setState(state => ({
      page: Math.min(state.page + 1, this.props.children.length - 1),
      values,
  }))

  previous = () =>
    this.setState(state => ({
      page: Math.max(state.page - 1, 0),
  }))

  handleSubmit = (values, bag) => {
    const { children, onSubmit } = this.props
    const { page } = this.state
    const isLastPage = page === React.Children.count(children) - 1
    if (isLastPage) {
      return onSubmit(values, bag)
    } else {
      bag.setTouched({})
      bag.setSubmitting(false)
      this.next(values)
      console.log("Next")
    }
  }

  render() {
    const { children } = this.props
    const { page, values } = this.state
    const activePage = React.Children.toArray(children)[page]
    const isLastPage = page === React.Children.count(children) - 1

    return (
      <Formik
        initialValues = {values}
        enableReinitialize = {false}
        validationSchema = {SignUpSchemas[page]}
        onSubmit = {this.handleSubmit}
        render = {props => (
          <>
            {!isLastPage ? <Heading size = "h1"> Sign Up! </Heading> : <Heading size = "h1"> What apps do you have? </Heading>} 
            <StyledForm>
              {React.cloneElement(activePage, { parentState: {...props} })}
              <Wrapper>
                            <Button> Previous </Button>
                            <Button
                                disabled = {!isValid || isSubmitting}
                                loading = {loading ? 'Logging in...' : null}
                                type = "submit"
                            > 
                                Log In 
                            </Button>
                        </Wrapper>

                        <MessageWrapper>
                            <Message error show = {error}>{ error }</Message>
                        </MessageWrapper>
            </StyledForm>
          </>
        )}
      />
    )
  }
}

export default Wizard
