import React, { Component } from 'react'
import onClickOutside from 'react-onclickoutside'
import styled from 'styled-components'

import Button from '../../../../style/FormUI/Buttons.js'

const StyledDropdown = styled.div`
    font-size: 2rem;
    width: 100%;
    height: ${props => props.opened ? "70rem" : "7rem"};
    border: 1px solid var(--color-border);
    position: relative;
    transition: ${props => props.opened ? "all 250ms ease-in" : "all 150ms ease-out"};

    main {
        max-height: ${props => props.opened ? "100vh" : "0"};
        opacity: ${props => props.opened ? "1" : "0"}
        transition: ${props => props.opened ? "all 250ms ease-in" : "all 150ms ease-out"};
    }
`

class Dropdown extends Component {
    constructor(props) {
        super(props)
        this.state = { childrenOpen: false }
    }

    handleClickOutside = () => {
        this.setState({ childrenOpen: false})
    }

    toggle = () => {
        this.setState(prevState => ({
            childrenOpen: !prevState.childrenOpen
        }))
    }

    render() {
        const {children, title} = this.props
        const {childrenOpen} = this.state

        return (
            <StyledDropdown opened = {childrenOpen}>
                {title}
                <Button type = "button" onClick = {this.toggle}> Dropdown </Button>
                <main>
                    {childrenOpen && children}
                </main> 

            </StyledDropdown>
        )
    }
}

export default onClickOutside(Dropdown)
