import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import { compose } from 'recompose';
import * as ROUTES from '../../constants/routes';
import { withFirebase } from '../Firebase';

import { Formik, Form, Field, ErrorMessage, FormikProps, withFormik } from 'formik'
import * as yup from 'yup'

const SignUpFormBase2 = ({
    errors,
    touched,
}) => (
    <Form className = "classic-form">
        <div className = "aligned-inputs"> 
            <div className = {touched.firstName && errors.firstName && "error"}>
                <label> First Name </label> <br />
                <Field name = "firstName" type = "text"/> <br />
                <ErrorMessage render = {msg => <p className = "error-text"> {msg} </p>} name = "firstName" />
            </div>

            <div className = {touched.lastName && errors.lastName && "error"}>
                <label> Last Name </label> <br />
                <Field name = "lastName" type = "text"/> <br/>
                <ErrorMessage render = {msg => <p className = "error-text"> {msg} </p>} name = "lastName" />
            </div>
        </div>

        <div className = {["compensate-input", touched.username && errors.username && "error"].join(' ')}>
            <label> Username </label> <br />
            <Field name = "username" type = "text"/> <br/>
            <ErrorMessage render = {msg => <p className = "error-text"> {msg} </p>} name = "username" />
        </div>

        <div className = {["compensate-input", touched.email && errors.email && "error"].join(' ')}> 
            <label> Email </label> <br />
            <Field name = "email" type = "text"/> <br/>
            <ErrorMessage render = {msg => <p className = "error-text"> {msg} </p>} name = "email" />
        </div>

