import React from 'react'
import { Link } from 'react-router-dom'
import * as ROUTES from '../../constants/routes'
import { Form, Field, ErrorMessage, Formik } from 'formik'
import * as yup from 'yup'
import { connect } from 'react-redux'
import * as actions from '../../backend/store/actions'
import { auth } from 'firebase';

const SignInSchema = yup.object().shape({
  email: yup.string("Must be a valid email").email("Must be a valid email").required("Please enter an email"),
  password: yup.string().min(8, "Password must be at least 8 characters").required("Please enter a password"),
})

const SignInForm = ({ login, loading, error }) => {
  console.log(error) // remove this when you get error to show
  return(
    <Formik
      initialValues = {{
        email: "",
        password: "",
      }}
      validationSchema = {SignInSchema}
      onSubmit = {( values, { resetForm, setSubmitting }) => {
        setTimeout(() => {
          signUp(values)
          resetForm()
          setSubmitting(false)
      }, 2000)
      }}
    >
      {({ errors, touched, isSubmitting }) => (
        <Form className = "classic-form">

          <div className = {["text-input", touched.email && errors.email && "text-error"].join(' ')}> 
            <label> Email </label> <br />
            <Field name = "email" type = "email"/> <br/>
            <ErrorMessage render = {msg => <p className = "error-msg"> {msg} </p>} name = "email" />
          </div>

          <div className = {["text-input", touched.passwordOne && errors.passwordOne && "text-error"].join(' ')}>
            <label> Password </label> <br />
            <Field name = "passwordOne" type = "password"/> <br/>
            <ErrorMessage render = {msg => <p className = "error-msg"> {msg} </p>} name = "passwordOne" />
          </div>

          {/* <p>{error}</p> Conditional rendering of the paragraph with styled components */}
          <button type = "submit" disabled = {isSubmitting} className = "classic-button"> Log In </button>
          </Form>
        )}
    </Formik>
  )
}

const mapStateToProps = ({ auth }) => ({
  loading: auth.loading,
  error: auth.error,
})

const mapDispatchToProps = {
  login: actions.signIn,
}

const SignInLink = () => (
  <pre className = "link-text">
    Already have an account?   <Link to={ROUTES.LANDING} className = "link">Log In</Link>
  </pre>
)

export { SignInLink }
export default connect(
    mapStateToProps,
    mapDispatchToProps
)(SignInForm)
