import React, { useState } from 'react'
import styled from 'styled-components'
import { connect } from 'react-redux'

const FixedWrapper = styled.header`
    position: sticky;
    z-index: 10;
    top: 8rem;
    width: 100%;
    height: 14rem
    display: flex;
    justify-content: center;
    border-bottom: 2px solid var(--color-border);
    margin-bottom: 3rem;
`
const Container = styled.div`
    width: 60%;
    display: flex;
    justify-content: space-between;
`
const Ul = styled.ul`
    position: absolute;
    display: flex;
    align-items: flex-end;
    justify-content: flex-start;
    height: 100%;
    width: 80%;
    border-bottom: 2px solid var(--color-title);
`
const Li = styled.li`
    display: flex;
    align-items: flex-end;
    justify-content: center;
    font-size: 2.5rem;
    font-weight: 700;
    letter-spacing: 0.7px;
    color: var(--color-title);
    padding: 0rem 5rem;
`
const StyledImage = styled.img`
    position: relative;
    border-radius: 50%; 
    align-items: center;
    display: block;
    top: -6rem;
    width: 18rem;
    height: 18rem;
    object-fit: cover;
`

const ProfileNavbar = ({ profile }) => {
    const [ordersOpen, setOrdersOpen] = useState(true)
    const [friendsOpen, setFriendsOpen] = useState(false)

    const handleOrdersClick = () => {
        setFriendsOpen(false)
        setOrdersOpen(true)
    }

    const handleFriendsClick = () => {
        setOrdersOpen(false)
        setFriendsOpen(true)
    }

    return (
        <FixedWrapper>
            <Container>
                <Ul>

                    <Li onClick = {handleOrdersClick} open = {ordersOpen}> Orders </Li>
                    <Li onClick = {handleFriendsClick} open = {friendsOpen}> Friends </Li>

                </Ul>
                <StyledImage src = {profile.image[0]} alt = "Profile" />
            </Container>
        </FixedWrapper>
    )
}

const mapStateToProps = ({ firebase }) => ({
    profile: firebase.profile,
})

export default connect(mapStateToProps)(ProfileNavbar);
