import React, { setState } from 'react'
import { connect } from 'react-redux'
import FileUploader from "react-firebase-file-uploader"
import * as actions from '../../../../backend/store/actions'
import { storage } from 'firebase'
import styled from 'styled-components'

const StyledImage = styled.img`
    border-radius: 50%; 
    align-items: center;
    display: block;
    margin-left: auto;
    margin-right: auto;
    width: 25rem;
    height: 25rem;
    object-fit: cover;
`

const ImageUpload = ({ updateImageUrl, profile }) => {
    const [avatar, setAvatar] = setState('')
    const [avatarUrl, setAvatarUrl] = setState(profile.imageUrl)

    const handleUploadSuccess = filename => {
        setAvatar(filename)
        storage()
            .ref("images/")
            .child(filename)
            .getDownloadURL()
            .then(url => {
                state.avatarURL = url
                updateImageUrl(state.avatarURL)
            }
        )
    }

    return (
        <form>
            <label>
                {state.avatarURL && <StyledImage src = {state.avatarURL}/>}
                <FileUploader
                    hidden
                    accept = "image/*"
                    name = "avatar"
                    randomizeFilename
                    storageRef = {storage().ref("images")}
                    onUploadSuccess = {handleUploadSuccess}
                />
            </label>
        </form>
    )
}

const mapStateToProps = ({ firebase }) => ({
    profile: firebase.profile,
})

const mapDispatchToProps = {
    updateImageUrl: actions.updateImageUrl,
}

export default connect(mapStateToProps, mapDispatchToProps)(ImageUpload);
