import React from 'react'
import styled from 'styled-components'

const StyledButton = styled.button`
  width: ${({ contain }) => (contain ? 'auto' : '30%')};
  outline: none;
  padding: 1.2rem 5rem;
  border-radius: 2rem;
  font-size: 1.2rem;
  color: var(--color-white);
  font-weight: 700;
  background-color: ${({ color }) => {
    if (color === 'red') return 'red'
    else if (color === 'main') return 'var(--color-maintitle)'
    else return 'var(--color-white)'
  }};
  margin: 1.5rem 0 2rem 0;
  border: none;
  transition: all 0.2s;

  &:hover {
    transform: translateY(-3px);
  }
  &:active {
    transform: translateY(2px);
  }
  &:disabled {
    cursor: not-allowed;
    background-color: #333;
  }
`

const Button = ({ children, disabled, loading, contain, color, ...rest }) => {
  return (
    <StyledButton color = {color} contain = {contain} disabled = {disabled} {...rest}>
      {loading ? loading : children}
    </StyledButton>
  )
}

export default Button
