import React from 'react'
import { connect } from 'react-redux'
import FileUploader from "react-firebase-file-uploader"
import * as actions from '../../../../backend/store/actions'
import { storage } from 'firebase'
import styled from 'styled-components'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChevronCircleLeft, faChevronCircleRight } from '@fortawesome/free-solid-svg-icons'

const ImageForm = styled.form`
    margin-bottom: 1rem;
    width: 74%;
    display: flex;
    align-items: center;

    div, label {
        opacity: 0;
        transition: all 0.5s;
    }

    :hover div, :hover label {
        opacity: 0.6;
    }
`
const StyledImage = styled.img`
    border-radius: 50%; 
    align-items: center;
    display: block;
    margin-left: auto;
    margin-right: auto;
    width: 25rem;
    height: 25rem;
    object-fit: cover;
`

const ImageUpload = ({ updateImageUrl, profile }) => {
    if (!profile.isLoaded) return null

    let state = {
        avatar: "",
        avatarURL: profile.image[0],
        oldAvatar: profile.image[1]
    }

    const handleUploadSuccess = filename => {
        state.avatar = filename
        storage()
            .ref("images/")
            .child(filename)
            .getDownloadURL()
            .then(url => {
                state.avatarURL = url
                updateImageUrl(state)
            }
        )
    }

    return (
        <ImageForm>
            <label>
                <FontAwesomeIcon icon = {faChevronCircleLeft} size = "5x" color = "#2C3E50" />
                <FileUploader
                    hidden
                    accept = "image/*"
                    name = "avatar"
                    randomizeFilename
                    storageRef = {storage().ref("images")}
                    onUploadSuccess = {handleUploadSuccess}
                />
            </label>
            {state.avatarURL && <StyledImage src = {state.avatarURL}/>}
            <div> <FontAwesomeIcon icon = {faChevronCircleRight} size = "5x" color = "#2C3E50"/> </div>
        </ImageForm>
    )
}

const mapStateToProps = ({ firebase }) => ({
    profile: firebase.profile,
})

const mapDispatchToProps = {
    updateImageUrl: actions.updateImageUrl,
}

export default connect(mapStateToProps, mapDispatchToProps)(ImageUpload);
