import React from 'react'
import styled from 'styled-components'

const InputWrapper = styled.div`
  width: 100%;
  display: flex;
  position: relative;
  margin-bottom: 3rem;
  flex-direction: column;
`
const Error = styled.p`
  color: var(--color-main);
  visibility: ${({ show }) => (show ? 'visible' : 'hidden')};
  opacity: ${({ show }) => (show ? '1' : '0')};
  transform: translateY(${({ show }) => (show ? '23px' : '10px')});
  transition: all 0.1s;
  position: absolute;
  bottom: 0;
  left: 0;
  padding: 0rem 0.7rem;
  font-weight: 400;
  font-size: 1.3rem;
`
const Label = styled.label`
  font-size: 1.3rem;
  width: 100%;
  padding: 0.7rem;
  color: var(--color-title);
`

const StyledTextInput = styled.input`
  padding: 1.2rem 2rem;
  width: 100%;
  background-color: #F4F6F6;
  color: var(--color-title);
  font-weight: 500;
  letter-spacing: 0.5px;
  font-size: 1.3rem;
  border-radius: 1rem;
  border: 1px solid transparent;
  transition: all 0.5s;

  &:hover {
    border: 1px solid var(--color-main);
  }

  &:focus {
    border-bottom: 1px solid var(--color-main);
    background-color: #eef1f1;
  }
`

export const TextInput = ({ label, field, form: { touched, errors }, ...props }) => {
  return (
    <InputWrapper>
      <Label> {label} </Label>
      <StyledTextInput {...field} {...props} />
      <Error show = {errors[field.name] && touched[field.name]}>
        {errors[field.name]}
      </Error>
    </InputWrapper>
  )
}

const StyledCheckboxInput = styled.input`
  list-style-type: none;
  display: grid;
  grid-template-columns: 143px 143px;
  grid-row: auto auto;
  grid-column-gap: 20px;
  grid-row-gap: 20px;
  padding-inline-start: 0%;
  margin-block-start: 0%;
  margin-block-end: 60px;

  li {
    display: inline-block;
  }

  input[type="checkbox"] {
    display: none;
  }

  label {
    border: 1px solid #fff;
    padding: 10px;
    display: block;
    position: relative;
    margin: 10px; 
    cursor: pointer;
  }

  label:before {
    background-color: white;
    color: white;
    content: " ";
    display: block;
    border-radius: 50%;
    border: 1px solid #ff9933;
    position: absolute;
    top: -5px;
    left: -5px;
    width: 25px;
    height: 25px;
    text-align: center;
    line-height: 28px;
    transition-duration: 0.4s;
    transform: scale(0);
  }

  label img {
    height: 100px;
    width: 100px;
    transition-duration: 0.3s;
    transform-origin: 50% 50%;
    border-radius: 20px;
  }

  :checked + label {
    border-color: #ff9933;
    border-radius: 20px;
  }

  :checked + label:before {
    content: "âœ“";
    background-color: #ff9933;
    transform: scale(1);
  }

  :checked + label img {
    transform: scale(0.9);
    z-index: -1;
  }
`

export const CheckboxInput = ({ label, field, form , ...props }) => {
  return <StyledCheckboxInput {...field} {...props} />
}
