import styled from 'styled-components'

import MenuNavbar from './MenuNavbar.js'
import MenuItem from './MenuItem.js'
import { menuData } from './menuData.js'
import Header from '../../../images/Dominos_Pizza.jpg'

const Wrapper = styled.div`
    width: 100%;
    display: flex;
    flex-direction: column
    justify-content: center;

    > header {
        height: ${props => props.onScroll ? "10rem" : "20rem"};
    }
`
const HeaderImg = styled.header`
    top: 8rem;
    position: fixed;
    z-index: 2;
    width: 100%;
    height: 20rem;
    overflow: hidden;
`
const Wrapper2 = styled.div`
    top: 20rem;
    position: relative;
    z-index: 1;
`

const MenuItems = () => {
    const menu = menuData.map(item => <MenuItem key = {item.id} name = {item.name} picture = {item.src} description = {item.description}/>)

    let top
    let prevScrollpos = window.pageYOffset
