import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import { compose } from 'recompose';
import * as ROUTES from '../../constants/routes';
import { withFirebase } from '../Firebase';

const INITIAL_STATE = {
    firstName: '',
    lastName: '',
    username: '',
    passwordOne: '',
    passwordTwo: '',
    email: '',
    phone: '',
    error: false,
};

class SignUpFormBase extends Component {
    constructor(props) {
        super(props);
        this.state = { ...INITIAL_STATE };
    }

    handleSubmit = event => {
        const { username, email, phone, passwordOne, passwordTwo } = this.state;

        const isInvalid = (
            passwordOne !== passwordTwo ||
            passwordOne === '' ||
            email === '' ||
            phone === '' ||
            username === ''
        )
        event.preventDefault();

        if (isInvalid === true) {
            if (passwordOne === '' || email === '' || phone === '' || username === ''){
                alert("Please fill out all fields")
            } else {
                alert("Passwords must match")
            }
        } else {
            this.props.firebase
                .doCreateUserWithEmailAndPassword(email, passwordOne)
                .then(authUser => {
                // Create a user in your Firebase realtime database
                return this.props.firebase
                    .user(authUser.user.uid)
                    .set({
                    username,
                    email,
                    phone,
                    })
                })
                .then(() => {
                this.setState({ ...INITIAL_STATE });
                this.props.history.push('/pay');
                })
                .catch(error => {
                this.setState({ error });
                });
        }
    }

    handleChange = event => {
        this.setState({ [event.target.name]: event.target.value });
    };

    componentDidUpdate = () => {
        if (!this.state.error)
        this.setState({ error: true })
    }

    validate = () => {
        return {
            firstName: this.state.firstName.length == 0,
            lastName: this.state.lastName.length == 0,
            username: this.state.username.length == 0,
            passwordOne: this.state.passwordOne.length == 0,
            passwordTwo: this.state.passwordTwo.length == 0 || this.state.passwordTwo != this.state.passwordOne,
            phone: this.state.phone.length == 0,
            email: this.state.email.length == 0
        }
    }


    render() {
        const errors = this.state.error ? this.validate : INITIAL_STATE

        return (
            <form onSubmit={this.handleSubmit} className = "classic-form">

                <div className = "aligned-inputs"> 
                    <div className = {errors.firstName && "error"}>
                        <label> First Name </label> <br />
                        <input
                        name = "firstName"
                        value = {this.state.firstName}
                        onChange = {this.handleChange}
                        type = "text"
                        /> <br />
                    </div>

                    <div className = {errors.lastName && "error"}>
                        <label> Last Name </label> <br />
                        <input
                        name = "lastName"
                        value = {this.state.lastName}
                        onChange = {this.handleChange}
                        type = "text"
                        /> <br/>
                    </div>
                </div>

                <div className = {["compensate-input", errors.username && "error"].join(" ")}>
                    <label> Username </label> <br />
                    <input
                    name = "username"
                    value = {this.state.username}
                    onChange = {this.handleChange}
                    type = "text"
                    /> <br/>
                </div>

                <div className = {["compensate-input", errors.email && "error"].join(" ")}> 
                    <label> Email </label> <br />
                    <input
                    name = "email"
                    value = {this.state.email}
                    onChange = {this.handleChange}
                    type = "text"
                    /> <br/>
                </div>

                <div className = {["compensate-input", errors.phone && "error"].join(" ")}>
                    <label> Phone Number </label> <br />
                    <input
                    name = "phone"
                    value = {this.state.phone}
                    onChange = {this.handleChange}
                    type = "text"
                    /> <br/>
                </div>

                <div className = "aligned-inputs"> 
                    <div className = {errors.passwordOne && "error"}>
                        <label> Password </label> <br />
                        <input
                        name = "passwordOne"
                        value = {this.state.passwordOne}
                        onChange ={ this.handleChange}
                        type = "password"
                        /> <br/>
                    </div>

                    <div className = {errors.passwordOne && "error"}>
                        <label> Confirm Password </label> <br />
                        <input
                        name = "passwordTwo"
                        value = {this.state.passwordTwo}
                        onChange = {this.handleChange}
                        type = "password"
                        /> <br/>
                    </div>
                </div>

                <button onMouseOver = {this.handleClick} type = "submit" className = "classic-button"> Next </button>

            </form>
        );
    }
}

const SignUpForm = compose(
    withRouter,
    withFirebase,
)(SignUpFormBase);

const SignUpLink = () => (
    <pre className = "link-text">
      New to FeastMode?   <Link to={ROUTES.SIGN_UP} className = "link">Sign Up</Link>
    </pre>
);

export default SignUpForm
export { SignUpLink, INITIAL_STATE };
