import React from 'react'
import styled from 'styled-components'
import { NavLink } from 'react-router-dom'

const Li = styled.li`
  display: flex;
  height: 100%;
  padding: 5rem 5rem;
  align-items: center;
  justify-content: center;
`
const StyledNavLink = styled(NavLink)`
  display: flex;
  text-transform: uppercase;
  align-items: center;
  border-bottom: 2px solid transparent;
  font-size: 1.4rem;
  padding: 1rem;
  margin: 0 1rem;
  font-weight: 700;
  color: var(--color-title);
  transition: all 0.2s;

  :hover {
    border-bottom: 2px solid var(--color-main);
  }

  &.active {
    border-bottom: 2px solid var(--color-main);
  }
`

const MenuNavItem = ({ link, children, clicked }) => {
  return (
    <Li>
      <StyledNavLink onClick = {clicked} exact activeClassName = "active" to = {link}>
        {children}
      </StyledNavLink>
    </Li>
  )
}

export default MenuNavItem
