import React, { useState } from 'react'
import styled from 'styled-components'
import { connect } from 'react-redux'

import ImageUpload from "./ProfileEdit/ImageUpload.js"

const FixedWrapper = styled.header`
background-color: var(--color-main);
    position: sticky;
    z-index: 10;
    top: 8rem;
    width: 100%;
    height: 14rem
    display: flex;
    justify-content: center;
    border-bottom: 2px solid var(--color-border);
    margin-bottom: 3rem;
`
const Container = styled.div`
    width: 60%;
    display: flex;
    justify-content: space-between;
`
const Ul = styled.ul`
    display: flex;
    align-items: flex-end;
    height: 100%;
    width: 40%;
`
const Li = styled.li`
    position: relative;
    bottom: -2px;
    display: flex;
    align-items: flex-end;
    justify-content: center;
    font-size: 2.5rem;
    font-weight: 700;
    letter-spacing: 0.8px;
    color: var(--color-title);
    padding: 0rem 2rem;
    margin-right: 5rem;
    transition: all 0.2s;
    border-bottom: ${({ open }) => open ? "2px solid var(--color-title)" : "2px solid transparent"};

    :hover {
        border-bottom: 2px solid var(--color-title);
    }
`
const StyledImage = styled.img`
    position: relative;
    border-radius: 50%; 
    align-items: center;
    display: block;
    top: -6rem;
    width: 18rem;
    height: 18rem;
    object-fit: cover;
`

const ProfileNavbar = ({ profile }) => {
    const [ordersOpen, setOrdersOpen] = useState(true)
    const [friendsOpen, setFriendsOpen] = useState(false)
    const [accountOpen, setAccountOpen] = useState(false)

    const handleOrdersClick = () => {
        setFriendsOpen(false)
        setAccountOpen(false)
        setOrdersOpen(true)
    }
    const handleFriendsClick = () => {
        setOrdersOpen(false)
        setAccountOpen(false)
        setFriendsOpen(true)
    }
    const handleAccountClick = () => {
        setOrdersOpen(false)
        setFriendsOpen(false)
        setAccountOpen(true)
    }

    return (
        <FixedWrapper>
            <Container>
                <Ul>

                    <Li onClick = {handleOrdersClick} open = {ordersOpen}> Orders </Li>
                    <Li onClick = {handleFriendsClick} open = {friendsOpen}> Friends </Li>
                    <Li onClick = {handleAccountClick} open = {accountOpen}> Account </Li>

                </Ul>
                <ImageUpload />
            </Container>
        </FixedWrapper>
    )
}

const mapStateToProps = ({ firebase }) => ({
    profile: firebase.profile,
})

export default connect(mapStateToProps)(ProfileNavbar);
