import React, { useState } from 'react'
import styled from 'styled-components'

import ImageUpload from "./ProfileEdit/ImageUpload.js"

const FixedWrapper = styled.header`
    position: sticky;
    z-index: 10;
    top: 8rem;
    width: 100%;
    height: 14rem
    display: flex;
    justify-content: center;
    border-bottom: 2px solid var(--color-border);
    margin-bottom: 3rem;
`
const Container = styled.div`
    width: 60%;
    display: flex;
    justify-content: space-between;
`
const Ul = styled.ul`
    display: flex;
    align-items: flex-end;
    height: 100%;
    width: 40%;
`
const Li = styled.li`
    position: relative;
    bottom: -2px;
    display: flex;
    align-items: flex-end;
    justify-content: center;
    font-size: 2.5rem;
    font-weight: 700;
    letter-spacing: 0.8px;
    color: var(--color-title);
    padding: 0rem 2rem;
    margin-right: 5rem;
    transition: all 0.2s;
    border-bottom: ${({ open }) => open ? "2px solid var(--color-title)" : "2px solid transparent"};

    :hover {
        border-bottom: 2px solid var(--color-title);
    }
`
const Div = styled.div`
`

const ProfileNavbar = () => {
    const [open, setOpen] = useState([true, false, false])

    const handleClick = index => {
        let state = [false, false, false]

        setOpen([false, false, false])
        setOpen(open[index] == true)
    }

    return (
        <FixedWrapper>
            <Container>
                <Ul>

                    <Li onClick = {() => handleClick(0)} open = {open[0]}> Orders </Li>
                    <Li onClick = {() => handleClick(1)} open = {open[1]}> Friends </Li>
                    <Li onClick = {() => handleClick(2)} open = {open[2]}> Account </Li>

                </Ul>
                <ImageUpload />
            </Container>
        </FixedWrapper>
    )
}

export default ProfileNavbar
